import React, { useState } from "react";
import { Connect, connect } from "react-redux";
import { Link } from "react-router-dom";
import { Helmet } from "react-helmet";
import { setAlert } from "../actions/alert";
import { signup } from "../actions/auth";
import PropTypes from "prop-types";

const SignUp = ({ setAlert, signup, isAuthenticated }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    password2: "",
  });
  const { name, email, password, password2 } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });
  const onSubmit = (e) => {
    e.preventDefault();

    if (password != password2) {
      setAlert("Password do not match", "error");
    } else {
      signup(name, email, password, password2);
    }
  };

  if (isAuthenticated) {
    return <redirect to="/" />;
  }
  return(
    <div className='auth'>
      <Helmet>
        <title>Realest Estate -Sign Up</title>
        <meta name="description" content="Login page"/>
      </Helmet>
      <h1 className='auth__title'>Sign Up</h1>
      <p className='auth__lead'>Create  your Account</p>
      <form className='auth__form' onSubmit={e =>onSubmit(e)}>
        <div className='auth__form__group'>
          <input className='auth__form__input' type='text' placeholder='Name' name='name'value={name} onChange={e => onChange(e)} required />
        </div>
        <div className='auth__form__group'>
          <input className='auth__form__input' type='email' placeholder='Email' name='email'value={email} onChange={e => onChange(e)} required />
        </div>
        <div className='auth__form__group'>
          <input className='auth__form__input' type='password' placeholder='Password' name='password'value={password} onChange={e => onChange(e)} required minLength='6'/>
        </div>
        <div className='auth__form__group'>
          <input className='auth__form__input' type='password' placeholder='Confirm Password' name='password2'value={password} onChange={e => onChange(e)} required minLength='6'/>
        </div>
        <button className='auth__form__button' type='submit'>Register</button>
      </form>
      <p className='auth__authtext'>  Already have an account? <Link className='auth__authtext__link' to='/login'>Login</Link></p>
    </div>
  )
};

SignUp.propTypes = {
  setAlert: PropTypes.func.isRequired,
  signup: PropTypes.func.isRequired,
  isAuthenticated: PropTypes.bool
}
const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated
})
export default connect(mapStateToProps,{setAlert, signup })(SignUp);
